insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (1, 'Pha nước chanh với sữa tươi, quấy đều, để yên 15 phút rồi dùng. Sữa sẽ kết tủa và hơi chua do tác dụng của men. Dùng sữa này làm bánh sẽ giúp bánh có vị đậm đà và xốp hơn. Nếu các bạn ở nước ngoài và có thể mua được buttermilk thì các bạn có thể thay thế buttermilk cho sữa pha chanh này nhé', '', 1, 1);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (2, 'Nếu bạn dùng men khô, không phải loại “instant” hoặc muốn chắc chắn là men còn tốt thì có thể kích hoạt men bằng cách sau: Hâm nóng 60 ml sữa tươi, sao cho sữa ở nhiệt độ khoảng 35 – 40 độ C (không được nóng hơn, sẽ làm chết men hoặc yếu hoạt động của men). Cho 5gram đường và toàn bộ số men instant vào sữa, dùng đũa gỗ khuấy đều. Sau khoảng 5-10 phút sẽ thấy men nở tạo mảng, hơi giống với gạch cua. Nếu men không nở thì có hai khả năng, hoặc là men có vấn đề, hoặc là sữa quá nóng làm chết men, nên bỏ đi và chuẩn bị lại từ đầu.', '', 2, 1);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (3, 'Cho bột, đường và muối vào âu. Trộn đều. Cho men instant vào âu, trộn đều. Mục đích của việc này là để men không dính vào đường và muối (men dễ bị yếu đi hoặc chết). Nếu có kích hoạt men thì cho phần men cùng sữa vào ở bước sau.', '', 3, 1);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (4, 'Tạo một lỗ trống ở giữa âu bột. Cho sữa, sữa trộn chanh và trứng (đánh đều) vào âu bột. Quấy từ trong ra ngoài, tới khi nguyên liệu hòa quyện thành một khối đồng nhất.', '', 4, 1);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (5, 'Nhồi bột tới khi bột mịn và khá đàn hồi. Mình nhồi bằng máy cầm tay 500 Watt thì nhồi ở tốc độ thấp trong 3 – 4 phút đầu rồi tăng lên tốc độ vừa, sau 10 phút là đạt được mốc này. Nếu nhồi bằng tay có thể sẽ lâu hơn. Bột khá dính nên nếu nhồi tay, các bạn có thể bọc kín âu bột và để nghỉ 20 phút trước khi nhồi. Việc này giúp hình thành gluten trong khối bột, làm cho bột bớt ướt dính hơn.', '', 5, 1);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (6, 'Cho bơ mềm nhưng chưa chảy nước vào âu. Nhồi tiếp ở tốc độ chậm tới khi bơ hòa quyện hết thì tăng tốc độ, nhồi tới khi khối bột mịn, đàn hồi, có thể thử bằng window pane test: kéo dãn một ít bột, bột có thể căng tạo thành màng mỏng và không bị rách.', '', 6, 1);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (7, 'Đổ bột ra bàn có phủ một lớp bột khô mỏng. Dùng bột áo cho tay nếu bột quá dính. Tạo hình bột thành khối tròn có mặt mịn. Khối bột lúc này dẻo và có độ đàn hồi tốt, ấn thử ngón tay lên mặt khối bột sẽ thấy phồng trở lại. Ngoài ra, khối bột khá nhão và ướt, nếu sờ ngón tay lên mặt bột sẽ có cảm giác hơi dính, nhưng khi nhấc lên thì bột không dính vào tay.', '', 7, 1);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (8, 'Cho bột vào âu có quét một lớp dầu ăn mỏng. Lật khối bột cho dầu ăn bao đều bên ngoài khối bột, việc này sẽ tránh cho bột bị khô trong quá trình ủ. Dùng nilon bọc kín âu (hoặc dùng khăn ẩm đậy lên âu), ủ bột ở nhiệt độ phòng, đến khi bột nở gấp đôi. Để thử xem bột đã ủ đạt hay không, các bạn ấn một hoặc hai ngón tay vào bột, sâu khoảng 1 – 2cm. Sau khi rút tay lên, nếu vết lõm giữ nguyên tức là bột đã ủ đạt, nếu vết lõm phồng trở lại tức là cần ủ thêm thời gian.', '', 8, 1);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (9, 'Làm nóng lò nướng ở 170 độ C – hai lửa.', '', 1, 2);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (10, 'Cho lòng đỏ và 20 gram đường vào âu, đánh nhẹ nhàng tới khi đường tan hết, hòa quyện với lòng đỏ.', '', 2, 2);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (11,'Cho sữa, dầu ăn, vanilla vào, trộn đều với lòng đỏ', '', 3, 2);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (12, 'Rây bột mì và bột ngô, trộn đều tới khi hỗn hợp mịn mượt, không có vón cục.', '', 4, 2);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (13, 'Đánh lòng trắng trứng với muối, cream of tartar và đường tới bông cứng: lòng trắng bóng, dẻo, mịn, khi nhấc que đánh lên thấy có chóp nhọn, và chóp này không bị oặt xuống.  <br />
        * Lưu ý: lòng trắng, âu đựng và que đánh không được dính chất béo như lòng đỏ, dầu ăn, bơ, mỡ… nếu không sẽ không đánh bông được lòng trắng.', '', 5, 2);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (14, 'Trộn lòng trắng với hỗn hợp lòng đỏ bằng kĩ thuật fold. Sau khi trộn xong, hỗn hợp bông mịn, không có bọt khí to, không lỏng hoặc loãng. <br />
        * Lưu ý: <br />
        – Nếu các bạn lỡ tay đánh lòng trắng quá cứng, khi trộn lòng trắng vào thấy lòng trắng lổn nhổn thành từng cục to nhỏ (thay vì hòa quyện theo dạng sợi như trong video) thì nên dùng phới lồng quấy  nhẹ để lòng trắng hòa tan bớt vào hỗn hợp lòng đỏ, rồi mới chuyển sang dùng phới dẹt để fold. Hoặc có thể dùng hoàn toàn phới lồng để trộn nhưng với kiểu fold. <br />
        – Nếu sau khi trộn xong hỗn hợp bị lỏng, loãng, có nhiều bọt khí to thì nguyên nhân là do đánh bông trứng chưa đạt hoặc trộn sai kĩ thuật làm vỡ bọt khí trong trứng. Trường hợp này bánh nướng lên dễ bị dai và nở kém.', '', 6, 2);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (15, 'Nướng bánh ở chính giữa lò, 170 độ C trong 25 – 30 phút, tới khi mặt bánh chuyển vàng, ấn nhẹ lên mặt bánh thấy vết lõm lập tức phồng trở lại. <br />
            * Nhiệt độ nướng sẽ thay đổi tùy theo lò. Với lò nhỏ hoặc nhiệt độ chêcnh lệch không đều, có thể sẽ cần hạ thấp khay nướng và nướng ở nhiệt độ thấp hơn (150 – 160 độ C).', '', 7, 2);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (16, 'Lấy bánh chín ra khỏi khuôn, gỡ bỏ giấy nến. Để bánh nguội hẳn. Nên đặt bánh lên 1 chiếc khăn sạch, dưới khăn là rack và đặt tất cả trên 1 khuôn cao. Lớp khăn này sẽ giúp cho bánh không bị dính vào rack, đồng thời cũng thoát hơi nước dễ hơn.', '', 8, 2);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (17, 'Trong lúc đợi bánh nguội, đánh kem tươi với đường đến bông cứng. Khi kem đã gần đặc và có vân khi chạy máy, nên đánh ở tốc độ thấp nhất để tránh cho kem bị đánh quá đà và tách nước.', '', 9, 2);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (18, 'Cắt hoa quả thành miếng nhỏ. Để kem và hoa quả vào tủ lạnh khoảng 30 phút, kem sẽ đặc và trét bánh dễ hơn.', '', 10, 2);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (19, 'Khi bánh đã nguội, cắt bỏ phần rìa bánh vì phần này thường rất khô, sẽ làm gãy bánh khi cuộn. Đặt bánh lên 1 tờ giấy bạc, mặt nâu quay lên trên.', '', 11, 2);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (20, 'Trét kem lên mặt bánh, chừa lại khoảng 0.5 cm riềm hai bên và 2 cm ở mép cuối cuộn bánh (do khi cuộn kem sẽ dồn xuống dưới). Xếp hoa quả lên trên phần kem.', '', 12, 2);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (21, 'Nhẹ nhàng nhưng dứt khoát cuộn bánh lại. Giấy bạc có thể giúp cuộn bánh nếu bạn chưa quen tay vì giấy bạc khá cứng nên cuộn sẽ dễ hơn là dùng giấy nến hay khăn. Nên cuộn dứt khoát vì thường càng rón rén thì bánh càng dễ bị nứt.', '', 13, 2);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (22, 'Sau khi cuộn xong, bọc chặt bánh bằng nilon bọc thực phẩm. Để bánh vào tủ lạnh khoảng 3 – 4 giờ để phần kem lạnh hẳn. Bảo quản bánh trong ngăn  mát tủ lạnh trong khoảng 2 ngày. Bánh ngon hơn nhiều khi để lạnh', '', 14, 2);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (23, 'Chuẩn bị khuôn làm Flan. Thường thì mình làm trong ramekin (cốc sứ), nếu các bạn làm bằng cốc kim loại thì nên có phương pháp cách nhiệt như mình đã nói trong bài trước, để hạn chế việc hỗn hợp trứng sữa bị sôi, làm cho Flan bị tổ ong. Công thức ở trên làm được 8-10 cốc tùy kích thước. Các bạn cũng có thể làm trong khuôn to hơn. Lưu ý điều chỉnh nhiệt độ cho phù hợp với kích thước và chiều cao của khuôn là được. <br />
            Quét một lớp bơ mỏng lên thành trong của khuôn, không quét bơ ở đáy khuôn. Mục đích của việc này là để lấy flan ra dễ dàng hơn.', '', 1, 3);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (24, 'Chưng nước đường Caramel: <br />
            – Cho đường vào nồi nhỏ (bạn nào chưa quen thì nên dùng nồi sáng màu, sẽ dễ nhìn màu của nước đường cháy hơn là nồi mình dùng trong hình). Đổ nước vừa ngập đường, lắc nhẹ nồi cho đường và nước dàn đều. <br />
            – Để lửa vừa – to, bắc nồi lên bếp và để nguyên, không cần đụng vào gì cả, thi thoảng có thể lắc nhẹ nồi cho nước và đường cháy đều thôi. Đầu tiên nước sẽ sôi, sủi bong bóng to, sau đấy nước sẽ bay hơi bớt. Cuối cùng là nước đường từ từ chuyển sang màu vàng nhạt, màu mật ong rồi đậm hơn thành màu cánh gián… <br />
            – Nước đường chuyển màu rất nhanh. Nên chỉ cần đợi tới khi nước chuyển nâu vàng nhạt như màu mật ong là bạn có thể tắt bếp. Nhanh tay cho nước chanh vào, lắc hoặc khuấy đều rồi chia đều ra các khuôn. Không đợi quá lâu nước đường dễ bị đắng. <br />
            – Cần để cho nước đường này đông cứng hẳn lại rồi mới đổ phần sữa trứng cho Flan vào. Mùa đông trời hanh khô thì nước đường đông khá nhanh, những ngày trời nồm ẩm có thể sẽ phải để lâu hơn, tầm 5-10 phút. Nước đường không nhất thiết phải dàn được đều đáy khuôn, đóng thành một mảng cũng không sao. Sau này nó sẽ tự chảy ra và dàn đều, đẹp. <br />
            – Một vài chú ý nhỏ trong phần này là: <br />
            Cách làm này lâu hơn cách cho thẳng đường vào nồi rồi đun chảy, nhưng an toàn và khó hỏng hơn nhiều. Vì đường có nước, cháy chậm hơn nên rất dễ điều chỉnh màu sắc và độ cháy theo ý muốn. <br />
            Thường thì mình không chưng đường quá đậm màu. Khi nước đường chuyển sang màu đậm hơn mật ong là mình dừng lại rồi. Lý do chính là do thói quen không thích ăn quá đắng và dù sao thì đường cháy cũng không tốt cho sức khỏe nên mình cũng muốn hạn chế. Các bạn có thể điều chỉnh tùy theo sở thích của gia đình, nhưng lưu ý là đường chưng non thì vị sẽ rất ngọt, cho nên có thể giảm bớt lượng đường trong công thức. <br />
            Nước đường chưng rất rất nóng, khi đổ thì đổ khéo, cẩn thận bỏng nhé. <br />
            Nếu trong quá trình đổ nước đường ra khuôn mà nước đường bị đông lại thì đặt nồi lên bếp trở lại, đun vài giây là nó lại chảy ra.', '', 2, 3);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (25, 'Làm Flan <br />
            – Cho 3 trứng + 2 lòng đỏ vào âu. Cho đường. Dùng phới lồng cầm tay  hoặc nĩa/ dĩa nhẹ nhàng đánh trứng và đường cho tan bớt. Lưu ý: chỉ đánh tan trứng, không đánh bông. <br />
            – Cho sữa & kem tươi vào nồi. Đun lửa vừa và khuấy đều tay. Đến khi kem sữa ấm nóng (nóng hơn nước dùng để tắm một chút) thì bắc xuống. <br />
            – Từ từ đổ kem sữa vào âu trứng đường, vừa đổ vừa nhẹ nhàng khuấy đều. Cho vani, quấy đều. <br />
            – Lọc hỗn hợp kem sữa qua rây để loại hết các lợn cợn trứng nếu có. Chia vào các khuôn Flan <br />
            – Một vài chú ý trong phần này là:  <br />
                Rất nên đun kem sữa cho nóng rồi mới đổ vào trứng. Lý do là giúp kem trứng hòa quyện, trứng bớt tanh, giúp hạn chế rỗ, tóm lại là giúp bánh thơm ngon và đẹp hơn <br />
                Chỉ cần đun sữa tới ấm, không làm sữa quá nóng vì sẽ dễ làm trứng chín gây ra lợn cợn. Nếu lỡ đun quá nóng thì có thể quấy vài nhát cho sữa nguội bớt rồi mới đổ vào trứng.', '', 3, 3);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (26, 'Nướng hoặc hấp Flan đến chín: <br />
            a. Nướng cách thủy <br />
            – Vặn lò lên 150 độ trước 10 – 15 phút (hai lửa) <br />
            – Chuẩn bị khay để nướng cách thủy. Tốt nhất là nên dùng khay riêng, không dùng khay đen gắn trực tiếp với thành lò. Lót khăn ở đáy khay, giúp giảm nhiệt ở đáy flan và bánh không bị rỗ ở đáy. <br />
            – Cho khuôn Flan vào khay. Đổ nước sôi vào khay sao cho nước ngập khoảng 1/2 khuôn (không nên để mực nước thấp hơn). <br />
            – Nướng ở nhiệt độ 150 – 160 độ C đến khi Flan chín. Thời gian nướng dao động trong khoảng 25 – 40 phút tuỳ theo kích thước khuôn và nhiệt nướng. Sau khoảng 25 phút các bạn có thể thử flan bằng cách cắm một que tăm vào giữa bánh, rút lên không thấy nước chạy ra, tăm hơi ẩm, không quá ướt, khi lắc thử thấy mặt flan còn hơi sóng sánh thì tắt lò. <br />
            b. Hấp cách thủy <br />
            – Dùng nồi hấp hai tầng hoặc xửng, chõ hấp. Nếu không có xửng thì hấp trực tiếp như trong video cũng không sao. <br />
            – Đun sôi một ít nước trong nồi. Khi nước sôi thì hạ lửa nhỏ, đủ để nước sôi lăn tăn (nếu hấp bằng xửng) hoặc để cho nước không sôi nữa, nhưng đủ nóng (khoảng 90 – 95 độ C) nếu hấp trực tiếp trong nồi như trong video. <br />
            – Sau khi chia flan vào các khuôn, dùng giấy bạc che kín khuôn flan hoặc đậy nắp khuôn. Nếu không có vật dụng gì để che thì cần chuẩn bị hai chiếc khăn sạch: 1 cái che mặt khuôn Flan, 1 cái phủ lên miệng nồi (để hứng hơi nước đọng lại ở nắp vung, tránh cho Flan bị rỗ mặt). <br />
            – Xếp flan vào nồi hấp như trong video hoặc dùng xửng hấp. Hấp trong khoảng 25 – 35 phút hoặc lâu hơn tuỳ kích thước khuôn. Sau khoảng 20 – 25 phút thì kiểm tra flan giống như khi nướng cách thuỷ: cắm một que tăm vào giữa bánh, rút lên không thấy nước chạy ra, tăm hơi ẩm, không quá ướt, khi lắc thử thấy mặt flan còn hơi sóng sánh là đã đạt.', '', 4, 3);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (27, 'Flan sau khi chín thì để nguội rồi cho vào tủ lạnh tối thiểu 2-3 tiếng đồng hồ để lớp đường Caramel ở đáy khuôn chảy ra. Tốt nhất là để qua đêm.', '', 5, 3);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (28, 'Khi ăn ngâm cốc đựng flan vào cốc nước nóng khoảng 10 – 20 giây (tuỳ kích thước khuôn flan). Dùng đầu mũi dao chạy quanh thành trong của cốc. Nước nóng làm cho bơ ở thành khuôn chảy, giúp flan tự tách khỏi thành khuôn. Cách này rất dễ làm và hạn chế việc lách dao quá sâu vào làm vỡ thành flan. Úp đĩa lên mặt khuôn rồi lật ngược lại để lấy bánh ra. Và măm măm thôi ', '', 6, 3);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (29, 'Cho tất cả gia vị vào nồi nhỏ, đun nhỏ lửa cho đến khi tất cả hòa quyện với nhau <br />
            Thịt lợn mua về cắt thành những miếng to, mỏng dày 2,5 cm <br />
            Trộn đều gia vị và thịt, để thịt ngâm trong nước gia vị này trong ít nhất 2 giờ hoặc đậy kín để trong tủ lạnh qua đêm là ngon nhất.', '', 1, 4);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (30, 'Đặt thịt lên khay, cho vào lò nướng ở 180 độ C, với chế độ 2 lửa trong 45 phút. Khoảng 15 phút lấy thịt ra trở mặt và đổ thêm phần nước ướp lên trên.', '', 2, 4);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (31, 'Lưu ý: <br />
            - Không đun hỗn hợp ướp ở nhiệt độ quá cao, sẽ khiến đường khét đắng. <br />
            - Thịt ướp càng lâu sẽ càng ngon. <br />
            - Chỉ nướng thịt ở nhiệt độ vừa phải, tránh cho thịt bị cháy khô.', '', 3, 4);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (32, 'Đùi gà lọc xương ra riêng. Phần xương tận dụng nấu tô canh rau cải cho cả nhà luôn.', '', 1, 5);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (33, 'Trong một tô lớn bào nhuyễn táo trộn đều với nước mắm, đường, ngũ vị cho tan. Nêm nếm thử xem vừa độ mặn ngọt chưa để thêm gia vị cho hợp khẩu vị. Cho đùi gà vào ướp đều khoảng 15-30 phút.', '', 2, 5);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (34, 'Cho gà lên vỉ nướng có lót giấy chống dính. Cho vào lò nướng đã bật sẵn trước 250 độ C. Nướng 5 phút thì hạ lửa xuống 180 độ C nướng thêm 10 phút. Thấy gà chín vàng da lấy ra. <br />
                Trong thời gian chờ gà thì nấu cơm, rửa rau. Nấu canh. Gà chín cho ra đĩa ăn kèm rau sống cà chua.', '', 3, 5);




