insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (50, 'Rây bột và đường vào âu, trộn đều. Cho nước cốt dừa, sữa tươi và đập trứng vào âu. Trộn đều.',
            '', 1, 9);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (51, 'Lọc bột qua rây hai lần để loại bỏ các vụn bột vón. Bọc âu bột kín, để ngăn mát tủ lạnh 30 phút.',
            '', 2, 9);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (53, 'Trong khi đợi, đánh bông kem tươi với đường. Các bạn có thể xem hướng dẫn và các mẹo đánh bông kem tươi tại đây. Để kem tươi vào ngăn mát tủ lạnh tới khi dùng. <br />
                Ghi chú: nhiều công thức dùng gelatin với mục đích để giúp kem bông đứng hơn, tuy nhiên việc này không hoàn toàn cần thiết nên mình bỏ qua. Lưu ý là gelatin không giúp kem chậm chảy hơn. Ở nhiệt độ nóng khoảng trên 27 độ C, kể cả khi có gelatin thì kem tươi vẫn dễ bị mềm và chảy.',
            '', 3, 9);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (54, 'Lấy âu bột ra khỏi tủ lạnh. Quấy đều. Nếu muốn làm cả vị lá dứa thì múc lấy một nửa lượng bột rồi pha thêm chiết xuất lá dứa và màu thực phẩm xanh.',
            '', 4, 9);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (55, 'Làm nóng chảo chống dính trên lửa nhỏ. Cho bơ vào chảo, đợi bơ chảy hết rồi dùng giấy lau gần sạch hết bơ, chỉ để lại một lớp rất mỏng.',
            '', 5, 9);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (56, 'Quấy đều bột rồi múc một muỗng đổ vào chảo, nghiêng chảo để bột chảy dàn đều. Rán trên lửa nhỏ trong khoảng 1 – 1.5 phút. Ta sẽ thấy mặt bánh từ từ se dần lại. Khi mặt bánh khô và se, rìa bánh róc khỏi chảo dễ dàng thì lấy bánh ra ngoài. Để bánh nguội trên giá có khe hở. Tiếp tục rán phần bột còn lại cho tới hết. <br />
                * Lưu ý: <br />
                – Luôn quấy đều bột trước khi đổ bánh vì bột sẽ lắng xuống đáy bát trong khi chờ rán bánh. <br />
                – Rán ở lửa nhỏ. Nếu để lửa quá to, mặt dưới của bánh sẽ dễ bị cháy. Không rán quá lâu bánh dễ bị dai và cứng.',
            '', 6, 9);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (57, 'Khi bánh đã nguội, ta cho nhân vào bánh: Úp mặt bánh đẹp hơn xuống dưới (để mặt này quay ra ngoài sau khi gói). Múc một phần kem tươi để vào giữa bánh, thêm sầu riêng, gập bốn góc bánh để gói lại. Làm lần lượt tới khi hết bánh. <br />
                * Nếu không ăn được sầu riêng, bạn có thể dùng các loại quả khác như chuối, xoài, đào, kiwi, dâu… vì vỏ bánh này rất ngon nên dùng kèm với kem tươi và loại quả nào cũng ngon cả.',
            '', 7, 9);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (58, 'Việc sử dụng topping cream thay cho kem tươi là tuỳ khẩu vị. Cá nhân mình không ăn topping cream bao giờ vì mùi vị của topping cream không hấp dẫn với mình, hơn nữa lại là kem nhân tạo nên cũng không có chất bổ nào cho sức khoẻ.',
            '', 8, 9);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (60, 'Bạn có thể dùng kem trứng custard với món crepe này cũng rất ngon. Cách làm như sau: <br />
                 Đánh lòng đỏ trứng và đường tới khi lòng đỏ chuyển màu vàng nhạt, bông mịn <br />
                 Rây bột ngô vào trộn đều <br />
                 Từ từ đổ sữa ấm (nhưng không nóng) vào, vừa đổ vừa quấy để sữa hoà quyện với lòng đỏ và bột. <br />
                 Nấu hỗn hợp trên lửa vừa, quấy liên tục trong khi nấu. Khi kem chuyển đặc sệt thì bắc khỏi bếp, đổ ra bát để nguội. <br />
                 Nếu kem đặc nhưng có mùi trứng hoặc còn vị bột sống thì nguyên nhân là lửa nấu quá to làm nước bay hơi nhanh, hỗn hợp đặc nhanh nhưng trứng hay bột chưa chín hoàn toàn. Cách chữa: thêm vào hỗn hợp một ít sữa, quấy đều cho lỏng bớt rồi nấu tiếp trên lửa nhỏ tới khi chín hoàn toàn. <br />
                 Nếu hỗn hợp bị vón cục: có thể lọc qua rây. <br />
                 Bạn có thể trộn phần nhân custard này với sầu riêng (trộn khi custard còn ấm nóng). Hoặc đợi nguội bớt rồi trộn với kem tươi đánh bông cũng rất ngon. <br />',
            '', 9, 9);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (61, 'Bánh nên bọc kín bằng nilon bọc thực phẩm, hoặc để trong hộp kín. Bảo quản trong ngăn mát tủ lạnh và dùng trong 2 ngày.',
            '', 10, 9);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (62, 'Rửa sạch chanh leo. Bổ đôi, múc lấy phần ruột để riêng. Rửa lại vỏ chanh, bóc bỏ các phần màng bám trong vỏ nếu có.',
            '', 1, 10);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (63, 'Pha 500 ml nước với muối, cho vỏ chanh vào ngâm khoảng 30 phút.',
            '', 2, 10);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (64, 'Ngâm 2 g gelatin với 15 ml nước trong 10 – 15 phút tới khi gelatin nở mềm. Nếu dùng gelatin bột, hoà để gelatin tan trong nước.',
            '', 3, 10);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (65, 'Cho phần ruột chanh leo vào nồi (từ 5 quả mình lấy được khoảng 85 gr ruột). Thêm 50 ml nước. Bắc nồi lên bếp, nấu ở lửa vừa, quấy đều trong khi nấu. Khi nước bắt đầu bay hơi thì bắc khỏi bếp. Việc làm nóng chanh này sẽ giúp khâu lọc chanh dễ dàng hơn.',
            '', 4, 10);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (66, 'Lọc chanh qua rây, dùng thìa chà để thịt quả và nước rơi qua rây, bỏ hạt.',
            '', 5, 10);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (67, 'Lấy 50 ml (3 Tbsp + 1 tsp) từ phần nước chanh vừa lọc, cho vào bát riêng. Giữ phần còn lại để làm lớp thạch gương. Hoà đường vào phần 50 ml vừa lấy ra này, lượng đường cần dùng trong khoảng 15 – 25 gr. Nếm thử thấy vị chua ngọt cân bằng là được.',
            '', 6, 10);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (68, 'Quay gelatin trong lò vi sóng hoặc chưng cách thuỷ (như trong video) tới khi gelatin tan hoàn toàn, quấy đều để gelatin hoà trong nước, không bị dính ở thành hay đáy bát.',
            '', 7, 10);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (69, 'Đổ phần nước chanh và đường ở bước (6) vào gelatin tan chảy, quấy đều. Giữ ở nhiệt độ phòng.',
            '', 8, 10);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (70, 'Đánh kem tươi với 30 gram đường tới khi kem đạt bông mềm. Lưu ý: để đánh kem cho nhanh, nên dùng kem tươi lạnh và để âu đánh + que đánh vào tủ lạnh khoảng 30 phút trước khi đánh kem. Xem thêm hướng dẫn cách đánh bông kem tươi trong bài viết này. <br />
            * Chỉ đánh kem tươi tới bông mềm để tránh kem bị tách nước khi tiếp tục trộn với chanh leo.',
            '', 9, 10);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (71, 'Cho hỗn hợp chanh leo ở bước (8) vào kem tươi, trộn nhẹ nhàng, tránh quấy đảo mạnh tay làm kem tách nước. Sau khi trộn xong, hỗn hợp bông mịn đặc.',
            '', 10, 10);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (72, 'Lấy vỏ chanh leo từ âu nước muối ra, lau khô. Đặt vỏ chanh leo trên mặt phẳng, nếu vỏ chanh không đứng thẳng được thì cắt bớt phần đáy để vỏ chanh có thể đứng thẳng. Chia mousse vào vỏ chanh. Hoặc bạn có thể dùng các cốc nhỏ cỡ 70 – 80 ml để đựng mousse. <br />
            Sau khi chia mousse vào hết các cốc, để vào ngăn mát tủ lạnh khoảng 2 giờ, tới khi mousse đông đặc hẳn.',
            '', 11, 10);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (73, 'Làm phần thạch gương: <br />
                 – Ngâm 5 gram gelatin với 45 ml nước trong 10 – 15 phút tới khi gelatin nở mềm. Chưng cách thuỷ hoặc quay lò vi sóng để gelatin tan chảy như trong bước (7). <br />
                 – Đổ phần nước chanh leo còn thừa từ bước (6) vào hỗn hợp gelatin, quấy cho hoà quyện. Đong phần nước chanh gelatin này, thêm nước để có tổng cộng 160 ml (2/3 cup). Thêm đường cho vừa ăn (khoảng 20 – 30 gr), quấy tan đường. <br />
                 – Dùng thìa đổ nhẹ nhàng phần nước thạch lên trên mousse đã đông. Lưu ý đổ nhẹ tay, tránh làm hỏng mousse. Ngoài ra, phần nước thạch không được quá ấm nóng, nếu không cũng dễ làm mousse bị chảy. Để các cốc mousse vào ngăn mát tủ lạnh thêm 1 – 2 giờ tới khi lớp thạch gương đông hoàn toàn. <br />',
            '', 12, 10);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (74, 'Trang trí mousse với kem tươi đánh bông, hoa quả, kẹo chocolate… tùy thích. Bảo quản trong ngăn mát tủ lạnh, dùng trong 2 ngày. <br />
            * Lưu ý: cần bảo quản mousse trong tủ lạnh vì nếu để ở nhiệt độ khoảng từ 27 độ C trở lên, mousse sẽ mềm và chảy nước khá nhanh.',
            '', 13, 10);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (75, 'Làm nóng lò nướng ở 170 độ C – hai lửa. Lót giấy nến vào khuôn.',
            '', 1, 11);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (76, 'Cho lòng đỏ và 20 gram đường vào âu, đánh nhẹ nhàng tới khi đường tan hết, hòa quyện với lòng đỏ. Cho màu đỏ và hương dâu vào, trộn đều.',
            '', 2, 11);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (77, 'Cho sữa, dầu ăn, vanilla vào, trộn đều với lòng đỏ.',
            '', 3, 11);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (78, 'Rây bột mì và bột ngô, trộn đều tới khi hỗn hợp mịn mượt, không có vón cục. Nếu thấy hỗn hợp có màu chưa đủ đậm thì có thể cho thêm màu thực phẩm. Hỗn hợp bột và lòng đỏ nên có màu hồng sậm, khi trộn với lòng trắng, màu nhạt bớt sẽ thành vừa.',
            '', 4, 11);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (79, 'Đánh lòng trắng trứng với muối, cream of tartar và đường tới bông cứng: lòng trắng bóng, dẻo, mịn, khi nhấc que đánh lên thấy có chóp nhọn, và chóp này không bị oặt xuống. <br />
                * Lưu ý: lòng trắng, âu đựng và que đánh không được dính chất béo như lòng đỏ, dầu ăn, bơ, mỡ… nếu không sẽ không đánh bông được lòng trắng.',
            '', 5, 11);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (80, 'Trộn lòng trắng với hỗn hợp lòng đỏ bằng kĩ thuật fold. Sau khi trộn xong, hỗn hợp bông mịn, không có bọt khí to, không lỏng hoặc loãng. <br />
                 * Lưu ý: <br />
                 – Thông thường khi trộn lòng trắng với lòng đỏ ta hay dùng phới dẹt. Trong video này mình giới thiệu với các bạn cách dùng phới lồng để trộn, thường áp dụng trong trường hợp lỡ tay đánh lòng trắng quá cứng (khi trộn lòng trắng vào thấy lòng trắng lổn nhổn thành từng cục to nhỏ). <br />
                 – Nếu sau khi trộn xong hỗn hợp bị lỏng, loãng, có nhiều bọt khí to thì nguyên nhân là do đánh bông trứng chưa đạt hoặc trộn sai kĩ thuật làm vỡ bọt khí trong trứng. Trường hợp này bánh nướng lên dễ bị dai và nở kém.',
            '', 6, 11);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (81, 'Nướng bánh ở chính giữa lò, 165 – 170 độ C trong 25 – 30 phút, tới khi mặt bánh chuyển vàng, ấn nhẹ lên mặt bánh thấy vết lõm lập tức phồng trở lại. <br />
                 * Nhiệt độ nướng sẽ thay đổi tùy theo lò. Với lò nhỏ hoặc nhiệt độ chêcnh lệch không đều, có thể sẽ cần hạ thấp khay nướng và nướng ở nhiệt độ thấp hơn (150 – 160 độ C). Xem thêm hướng dẫn về cách chỉnh nhiệt lò trong hai bài viết sau: <br />
                 A-Z: Lò nướng – Kinh nghiệm chọn lò – Cách chỉnh lò để bánh không bị xẹp, lõm, thắt eo, ẩm, cháy… Phần 1 – Phần 2',
            '', 7, 11);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (82, 'Lấy bánh chín ra khỏi khuôn, gỡ bỏ giấy nến. Để bánh nguội hẳn. <br />
                 * Lưu ý: Nên đặt bánh lên 1 chiếc khăn sạch, dưới khăn là rack và đặt tất cả trên 1 khuôn cao. Lớp khăn này sẽ giúp cho bánh không bị dính vào rack, đồng thời cũng thoát hơi nước dễ hơn.',
            '', 8, 11);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (83, 'Trong lúc đợi bánh nguội, đánh kem tươi với đường đến bông cứng. Khi kem đã gần đặc và có vân khi chạy máy, nên đánh ở tốc độ thấp nhất để tránh cho kem bị đánh quá đà và tách nước. Xem hướng dẫn cách đánh kem và xử lí kem bị tách nước trong bài viết này. <br />
            Thái dâu tây thành lát mỏng. Để kem và dâu vào tủ lạnh khoảng 30 phút, kem sẽ đặc và trét bánh dễ hơn.',
            '', 9, 11);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (84, 'Khi bánh đã nguội, cắt bỏ phần rìa bánh bị khô, cắt đôi bạt bánh, ta sẽ có hai phần bánh hình chữ nhật bằng nhau.',
            '', 10, 11);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (85, 'Đặt phần bạt bánh thứ nhất lên đế bánh, lót một vài miếng giấy ở dưới bánh để tránh kem tươi dây bẩn ra đế bánh.',
            '', 11, 11);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (86, 'Lấy 1/4 lượng kem tươi đánh bông, trét lên mặt bánh. Xếp các lát dâu lên trên. Phủ thêm một phần kem tươi lên. Đặt bạt bánh thứ hai lên trên. Dùng một phần kem nhỏ, đủ để bao phủ toàn bộ bên ngoài bánh. Sau khi đã phủ kem xong, để bánh vào ngăn mát tủ lạnh khoảng 30 phút để kem đặc lại. Việc này giúp cho khâu phủ kem tiếp theo dễ dàng và đẹp hơn, không bị dính vụn bánh.',
            '', 12, 11);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (87, 'Phủ nốt phần kem còn lại lên bánh. Tới đây thì có hai lựa chọn: (1) tiếp tục trang trí hoa quả trên mặt bánh, ta sẽ có một ổ bánh to hoặc (2) cắt bánh thành nhiều miếng nhỏ rồi đặt hoa quả lên từng miếng bánh để trang trí. Khi cắt, dùng dao sắc và cắt dứt khoát. Lau sạch dao sau mỗi lát cắt. <br />
                Bảo quản bánh trong ngăn  mát tủ lạnh trong khoảng 2 ngày. Bánh ngon hơn nhiều khi để lạnh  :-)',
            '', 13, 11);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (88, 'Pha bột bánh cuốn và ướp thịt: <br />
                 – Bột bánh cuốn nên pha trước tối thiểu 2 giờ để thay nước ngâm bột như trong video, sẽ giúp bánh hết mùi bột nồng hoặc chua. Nếu pha được qua đêm thì càng tốt. <br />
                 – Thịt nên ướp trước 2 – 4 giờ để ngấm gia vị (mình thường ướp từ tối hôm trước rồi để tủ lạnh, ngăn mát, vì đã có gia vị rồi nên thịt sẽ không bị ôi thiu nhé). <br />
                 – Nước chấm cũng nên pha trước vài giờ để cho ngấu. Khi dùng mới băm nhỏ tỏi ớt (nếu dùng) và thả vào.',
            '', 1, 12);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (89, 'Khi tráng bánh <br />
                 – Chuẩn bị các dụng cụ gồm âu bột, nồi hấp, que lấy bánh, đĩa đựng bánh… sẵn sàng. <br />
                 – Nên dùng nhiều nước để hấp bánh. Luôn để nước sôi mạnh nếu không bánh sẽ khó chín. Mình để lửa ở mức 4/6 của bếp điện. <br />
                 – Nên nhúng khuôn tráng qua nước ấm, đặt lên miệng nồi, đợi khuôn ẩm mới bắt đầu tráng sẽ dễ hơn. <br />
                 – Đổ bột tráng bánh từ trong ra ngoài, cố gắng đổ đều tay, bánh mỏng hay dày có một phần phụ thuộc vào tay nghề tráng bánh. Việc này thì cần quen tay do làm nhiều chứ mình cũng không có mẹo gì cả. Nếu cảm thấy bột khó tráng mỏng thì thêm nước. Bánh dày do cách tráng nhưng bột đủ nước sẽ vẫn mềm, còn bánh bị dày bởi bột thiếu nước, không tráng mỏng được thường sẽ cứng. <br />
                 – Luôn quấy bột trước khi tráng bánh mới vì bột sẽ lắng xuống đáy trong lúc chờ. Khi gần hết âu bột có thể cần thêm nước vì bột thường đặc hơn. <br />
                 – Cần có nắp vung đậy để giữ hơi nước giúp bánh chín nhanh và mềm. Chọn nắp vung vừa với nồi sẽ tốt hơn vì tránh được nước chảy từ vung xuống bếp. <br />
                 – Căn giờ để đợi bánh vừa chín tới. Bánh chín thường trong và rất dễ lấy ra. Bánh chưa chín hoàn toàn khi ăn sẽ có vị bột, bánh có màu đục. Bánh bị để lâu quá sẽ ướt, dính và dễ rách khi lấy ra. Nếu lỡ để bánh quá lâu, nên mở vung đợi một chút cho hơi nước bay bớt rồi mới lấy bánh. <br />
                 – Khi lấy bánh, để cho dễ thì có thể dùng que hay phới gạt xung quanh rìa bánh cho róc rồi mới xiên vào trong để hất bánh lên. <br />
                 – Nên quét dầu ăn lên đĩa để bánh cuốn sau khi hấp chín để bánh không dính đĩa. Có thể quét cả một lớp mỏng dầu lên mặt bánh. Nếu tự phi hành, dùng dầu ăn phi hành để quét giúp bánh thơm ngon hơn. Nếu có rổ hay rá nhỏ có thể úp ngược rổ, để bánh cuốn lên rổ, bánh dễ thoát hơi nên sẽ nguội nhanh và ít dính như khi đặt lên đĩa. <br />
                 – Đợi bánh nguội bớt mới gấp hoặc chuyển sang đĩa khác nếu không bánh sẽ dính chập lại với nhau. <br />
                 – Nếu chỉ hấp bằng một nồi nhỏ thì thời gian làm bánh sẽ khá lâu. Mình làm cho 4 người mất tầm 2 – 2.5 tiếng. Để tiết kiệm thời gian các bạn có thể làm nồi to (giống nhiều hàng bánh cuốn) hoặc tráng 2 – 3 nồi một lúc. <br />
                 – Bánh không dùng hết có thể bảo quản trong hộp kín, để ngăn mát tủ lạnh từ 1 – 2 ngày. Khi dùng lại thì làm nóng với lò vi sóng (đậy kín để bánh không mất hơi nước và bị khô).',
            '', 2, 12);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (90, 'Khi nướng thịt <br />
                 – Có thể nướng thịt cùng lúc tráng bánh để tiết kiệm thời gian. <br />
                 – Nướng trên than là ngon nhất. Còn nếu nướng lò thì nên dùng khay có khe hở (mình dùng khay nướng pizza) hoặc xiên thịt vào que tre, nướng trên rack sẽ giúp thịt mềm ngon hơn vì không bị mất nhiều nước. <br />
                 Các lưu ý chắc là hết rồi, cũng hơi nhiều nên nếu chẳng may có bỏ sót mục nào mình sẽ thêm vào sau nhé. Có gì cần biết thêm mà chưa được giải thích rõ trong bài thì các bạn để lại câu hỏi ở dưới, mình sẽ cố gắng trả lời sớm. Chúc cả nhà có món bánh cuốn ưng ý  :-) <br />',
            '', 3, 12);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (91, 'Xem thêm video để biết chi tiết.',
            '', 1, 13);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (92, ' MỘT SỐ LƯU Ý ĐỂ LÀM NEM NGON: <br />
                 – Tỉ lệ mắm và đường có thể thay đổi tuỳ theo khẩu vị và loại mắm mà bạn dùng. Nên làm như trong video, sau khi trộn xong thì nêm nếm thử rồi mới cho phần gia vị cuối cùng vào trộn. <br />
                 – Theo hướng dẫn trên vỏ bao thì 1 gói gia vị dùng được cho 500 gr thịt và 250 gr bì nhưng tỉ lệ như vậy mình thấy nem hơi cứng nên tăng như trên thì thấy nem giòn mà vẫn đủ mềm. <br />
                 – Như đã giải thích trong video, mình thường làm nem chua bò vì cảm giác thịt bò ngon hơn, và vì thịt bò có thể ăn sống hoặc tái nên cũng thấy yên tâm hơn nữa. Thịt cần được xay nhuyễn mịn và luôn ở trạng thái rất lạnh, cả khi xay lẫn khi trộn (thịt lạnh sẽ cho nem giòn hơn). <br />
                 – Để nem được ngon và giữ được lâu, cần lưu ý giữ nguyên liệu khô ráo khi làm, bao gồm: bì lợn sau khi luộc xong phải để khô, thịt không bị chảy nước, các thứ tỏi ớt, rau răm sau khi rửa sạch cũng cần để khô mới dùng <br />
                 – Gói gia vị nhỏ từ túi bột làm nem sẽ làm cho thịt chuyển cứng khá nhanh. Bởi vậy sau khi trộn xong cần gói nem nhanh, không để quá lâu thịt cứng lại sẽ khó làm.',
            '', 2, 13);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (93, 'Xem thêm video để biết chi tiết.',
            '', 1, 14);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (94, '* Lưu ý: nhiệt độ và thời gian nướng ga-tô Hồng Kông có quyết định khá lớn trong việc bánh có nở cao xốp mềm căng, và có bị xẹp hay lõm không. Về cách chỉnh nhiệt nướng, các bạn có thể xem thêm bài viết “Tổng hợp các nguyên nhân làm bánh ga-tô bị xẹp, lõm mặt/ đáy, thắt eo và cách khắc phục“',
            '', 2, 14);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (95, 'Que tre sau khi mua về mình rửa sơ rồi luộc qua nước sôi. Theo mình học được trên mạng thì ngâm que tre vào nước lạnh trước khoảng nửa tiếng sẽ giúp que tre không bị cháy khi nướng. Tuy nhiên vì mình không nướng trực tiếp bằng lửa nên bỏ qua bước này.',
            '', 1, 15);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (96, 'Thịt thái miếng mỏng vửa (miếng thịt quá dày sẽ khó cuốn vào xiên, còn thịt mỏng quá thì sẽ mất nhiều thời gian cuốn). Hành tím, tỏi, sả băm nhỏ.',
            '', 2, 15);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (97, 'Ướp thịt với nước mắm, nước hàng, dầu hào, đường, hành, tỏi, sả, dầu vừng, hạt tiêu, vừng trắng (không bắt buộc). Để từ 2-3 tiếng cho ngấm. Thường mình ướp thịt từ tối hôm trước để ăn bữa trưa hôm sau, hoặc ướp từ sáng để ăn bữa tối. Khâu này rất quan trọng vì nếu thời gian ướp không đủ cho gia vị ngấm vào thịt thì món ăn coi như thất bại. <br />
                *Lưu ý: Mình không có định lượng cụ thể mắm, đường, gia vị… vì mỗi gia đình có khẩu vị khác nhau, và mỗi loại mắm, bột nêm cũng có độ mặn nhạt khác nhau. Nếu bạn không chắc chắn thì nên pha gia vị ướp ra một bát riêng, nêm nếm cho đủ mặn ngọt rồi mới trộn cùng thịt. Sau khi ướp xong, có thể nấu chín một miếng (bằng lò vi sóng hoặc bếp thường) để nếm thử.',
            '', 3, 15);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (98, 'Xiên thịt vào que tre. Thịt vụn và các dải thịt nhỏ cuốn vào trong. Miếng thịt lớn hơn thì cuốn ra ngoài xiên. Khi cuốn xong thì lấy tay nắn đều cho xiên thịt tròn đều. Cả nhà lưu ý là không nên làm xiên thịt quá dày và cuốn quá chặt vì sẽ mất thời gian nướng và khả năng ngoài cháy trong sống sẽ cao.',
            '', 4, 15);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (99, 'Mang thịt đi nướng. Ở nhà nướng được bằng than là ngon nhất. Nếu không, các bạn có thể nướng bằng lò với mức nhiệt 200 độ C trong 30 – 35 phút. Trong quá trình nướng cần lật mặt xiên thịt để thịt chín đều. <br />
                Thời gian đầu mình không có lò thì nướng trực tiếp trên chảo (chảo này mình mua ở IKEA, giá khá rẻ và rất ưng ý, các bạn có thể Google với từ khoá “IKEA grill pan” nếu quan tâm). Nếu dùng chảo nướng như mình thì các bạn phết một lớp mỏng dầu lên chảo rồi nướng ở lửa vừa. Trong lúc nướng thi thoảng trở xiên thịt cho thịt chín đều. Nếu nướng bằng chảo, nên làm xiên thịt dẹt một chút để thịt chín nhanh và đều cả trong lẫn ngoài. <br />
                Trong quá trình nướng (than, lò hay chảo), để thịt không bị khô, có thể quét một lớp mỏng dầu ăn lên bên ngoài xiên thịt. <br />
                Cách làm thì chỉ có vậy thôi là xong rồi  :-) Món này theo mình đánh giá thì không khó. Chỉ quan trọng nhất là khâu ướp thịt bằng gia vị gì và để đủ lâu cho ngấm thôi. Nếu không có xiên, các bạn cũng có thể thái miếng thịt bình thường, ướp như bình thường và nướng như nướng chả để ăn bún chả (hồi chưa có chảo nướng thì mình thậm chí còn nướng áp chảo bằng chảo thường, vẫn thấy rất ổn  :-))',
            '', 5, 15);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (101, 'Hoà tan 2 phần 160 ml nước với 5 gr rau câu của (A) và (B) trong 2 nồi nhỏ. Để ngâm bột rau câu trong tối thiểu 1 giờ sẽ giúp thạch về sau không bị chảy nước.
                  Trong thời gian đợi ngâm bột thạch rau câu, pha cà phê và cho khuôn làm thạch vào tủ lạnh để làm lạnh khuôn (giúp thạch đông nhanh hơn, tiết kiệm thời gian đổ thạch).',
            '', 1, 16);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (102, 'Làm phần thạch cà phê: <br />
                  Đun một phần nước pha bột rau câu trên lửa to, khi nước sôi và rau câu tan hết, nước chuyển trong thì tắt bếp, cho cà phê và đường vào quấy cho tan đều. Nếu có đường hay bột rau câu chưa tan hết thì nấu trên lửa vừa tới khi tan hoàn toàn. Hớt bọt trên mặt nước nếu có. Giữ ấm bằng cách đặt nồi trong chậu nước nóng hoặc đặt trên bếp ở mức lửa thấp nhất, thi thoảng quấy đều tránh để lớp trên mặt nồi thạch bị đông.',
            '', 2, 16);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (103, 'Làm phần thạch kem sữa dừa <br />
                  – Cho nước cốt dừa, sữa tươi, kem tươi vào bát, quấy đều. Lưu ý là hỗn hợp này không được lạnh. <br />
                  – Đun phần nước pha bột rau câu còn lại, giống như khi làm thạch cà phê. Khi nước sôi và rau câu tan hết thì cho đường và hỗn hợp sữa dừa vào, quấy đều cho tan. Nếu có đường hay bột rau câu chưa tan hết thì nấu trên lửa vừa tới khi tan hoàn toàn. Hớt bọt trên mặt nước nếu có. Giữ ấm bằng cách đặt nồi trong chậu nước nóng hoặc đặt trên bếp ở mức lửa thấp nhất, thi thoảng quấy đều tránh để lớp trên mặt nồi thạch bị đông.',
            '', 3, 16);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (104, 'Đổ thạch <br />
                  – Đặt khuôn làm thạch vào một khay lớn, đổ nước đá vào khay để giữ lạnh cho khuôn. Khuôn lạnh giúp thạch đông nhanh, tiết kiệm thời gian chờ đổ các lớp thạch. <br />
                  – Đổ một lớp thạch cà phê vào khuôn. Đợi khoảng 1 ~2 phút hoặc lâu hơn nếu lớp thạch dày, tới khi thạch tương đối đông nhưng chưa cứng hẳn. Nhẹ nhàng đổ một lớp thạch kem sữa dừa lên trên. Dùng thìa múc và đổ thấp tay, tránh rót mạnh sẽ dễ làm lớp thạch ở dưới bị tan chảy hoặc vỡ. Tiếp tục đợi cho lớp thạch kem này tương đối đông thì đổ một lớp cà phê. Làm lần lượt tới khi hết cả hai loại thạch. <br />
                   * Lưu ý: <br />
                  – Để các lớp thạch dính vào nhau, nên đổ lớp mới khi lớp thạch cũ vừa đủ đông nhưng chưa quá cứng. Khi ấn tay lên thấy thạch không dính tay nhưng có cảm giác hơi ướt. Tuy nhiên, không đổ thạch quá sớm khi lớp thạch trong khuôn chưa đông hẳn vì sẽ dễ làm hỏng lớp thạch này. <br />
                  – Giữ cho thạch trong nồi ấm nóng bằng cách ngâm nồi thạch vào chậu nước nóng hoặc đặt trên bếp ở mức lửa nhỏ nhất, thi thoảng quấy đều. Việc này giúp cho thạch không bị đông trong lúc chờ đổ lớp mới, và thạch ấm nóng cũng giúp các lớp thạch dính vào nhau dễ hơn. <br />
                  – Trong khi đổ thạch, không dịch chuyển khuôn vì sẽ làm cho các lớp thạch bị xê dịch, lẫn vào nhau và không đều, phẳng.',
            '', 4, 16);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (105, 'Hoà tan 2 phần 160 ml nước với 5 gr rau câu của (A) và (B) trong 2 nồi nhỏ. Để ngâm bột rau câu trong tối thiểu 1 giờ sẽ giúp thạch về sau không bị chảy nước.',
            '', 5, 16);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (106, 'Khi đã đổ xong thạch, để nguội bớt rồi cho khuôn vào để trong ngăn mát tủ lạnh trong tối thiểu 1 giờ, để thạch đông cứng hoàn toàn.',
            '', 6, 16);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (107, 'Khi thạch đã đông cứng, lấy ra khỏi khuôn, cắt nhỏ. Giữ lạnh và dùng trong 1 – 2 ngày. Có thể dùng thạch kèm nước đường pha nước cốt dừa hoặc cà phê sữa đá. Cách pha cà phê mình đã viết ở đầu bài rồi nhé  :-)',
            '', 7, 16);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (108, 'Làm thạch kem sữa (nên làm trước tối thiểu 4 giờ) <br />
                  – Lấy 150 ml sữa từ 300 ml sữa trong phần A, cho vào 3 bát, mỗi bát 50 ml. Chia Gelatin làm 3 phần, mỗi phần 7 gram, cho vào ngâm trong sữa trong tối thiểu 15 phút tới khi Gelatin nở mềm. <br />
                  – Đun sôi một chút nước trong nồi, khi nước sôi thì hạ lửa xuống mức nhỏ nhất. Đặt bát đựng Gelatin vào nồi, quấy liên tục. Hơi nóng từ nước sẽ giúp Gelatin tan từ từ. Quấy tới khi Gelatin tan hoàn toàn vào sữa, không còn một vụn hay gợn nhỏ nào trong bát. Lưu ý vét đáy bát, tránh để Gelatin bị đọng ở đáy bát. <br />
                  – Cho 100 ml kem tươi và 50 ml sữa còn lại vào bát. Nêm đường vào từng bát, mỗi bát khoảng 15 – 20 g đường tuỳ khẩu vị. Quấy đều để đường tan hết. Đổ hỗn hợp qua rây, cho vào khuôn hay hộp vuông, để nguội rồi cho vào tủ lạnh. <br />
                  – Làm tương tự với hai phần thạch còn lại (đun chảy Gelatin, hoà kem tươi, sữa, đường…) <br />
                  <br />
                  Để làm thạch vị trà xanh: Cho từng chút nước sôi một (từ phần 15 ml nước sôi) vào trà xanh, vừa đổ nước vừa quấy đều để trà xanh tan từ từ, chuyển thành dạng sệt. Múc khoảng 40 – 50 ml kem sữa tươi ở bước (3), hoà với nước trà xanh sệt này, quấy cho tan đều. Đổ phần sữa pha trà xanh này qua rây, vào lại bát đựng kem sữa, quấy đều cho hoà quyện. Lọc lại hỗn hợp một lần nữa rồi đổ vào khuôn, để nguội rồi cho vào tủ lạnh. Bột trà xanh rất khó tan nên làm theo cách này sẽ giúp bột trà tan hết và hoà quyện trong kem sữa đều hơn. <br />
                  Để làm thạch vị dâu: Dùng sy-rô dâu hoặc một vài giọt màu thực phẩm đỏ cùng với hương vị dâu, cho vào bát đựng kem sữa, quấy đều. <br />
                  <br />
                  *  Ghi chú chung cho phần thạch:  <br />
                  – Thạch nên được để trong ngăn mát tủ lạnh trong tối thiểu 3 – 4 giờ, tới khi đông hoàn toàn mới dùng. Thạch càng để lạnh lâu thì càng dẻo và chắc. Tuy nhiên không nên để thạch trong tủ lạnh quá 4 ngày. <br />
                  – Thông thường, lượng Gelatin đủ để làm đông 600 ml chất lỏng (như trong công thức) là 15 gram. Tuy nhiên, dùng 21 gram như công thức sẽ cho thạch sữa có độ dẻo và dai một chút. Nếu bạn thích ăn thạch rất mềm thì có thể giảm lượng Gelatin về 15 – 16 g. Nếu bạn muốn thạch cứng, dẻo và dai hơn nữa thì có thể tăng Gelatin. Càng nhiều Gelatin, thạch sẽ càng dẻo. Tuy nhiên, nếu dùng quá nhiều, đặc biệt là Gelatin dạng bột thì thạch có thể có mùi hơi khó chịu từ Gelatin. <br />
                  – Đun sữa có pha Gelatin theo kiểu cách thuỷ như trong công thức có ưu điểm là giúp cho sữa không bị sôi và mất chất. Kem tươi có thể “ăn sống”, không nên đun (như hướng dẫn của khá nhiều ct chè khúc bạch), vì đun nóng dễ làm chất béo trong kem bị tách nước. Khi thạch đông có thể có lớp váng trên mặt hoặc thạch bị tách làm hai lớp, phần nước và phần béo. <br />
                  – Bạn có thể dùng các loại màu thực phẩm hoặc hương vị khác để làm các loại thạch khác nhau, ví dụ dùng nước cà phê đặc hoặc bột ca cao để làm thạch sữa có màu nâu đen, dùng chiết xuất lá dứa (lá nếp) để tạo màu xanh… <br />
                  – Nếu thạch của bạn không đông thì có thể do một trong số các nguyên nhân sau: <br />

                  * Gelatin chất lượng không tốt <br />
                  Làm sai quy trình. Lưu ý là Gelatin luôn cần phải được ngâm nước mát cho nở rồi mới dùng nhiệt để làm tan chảy. Không cho Gelatin vào nước nóng ngay từ đầu. <br />
                  Trong khi đun chảy Gelatin, không quấy kĩ để Gelatin chưa tan hết hoặc dính vào thành hay đáy bát.',
            '', 1, 17);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (109, 'Chuẩn bị các nguyên liệu khác <br />
                  – Cho 1 lít nước cùng đường phèn vào nồi. Đun sôi và quấy nhẹ tay để đường tan hết. Rửa sạch lá dứa, buộc thắt nút lại cho gọn. Khi nước sôi và đường đã tan hết thì thả lá dứa vào nồi. Bắc khỏi bếp, để nguội rồi cho nước này vào ngăn mát tủ lạnh, giữ lạnh tới khi dùng. Có thể đun lá dứa trong nước, mùi lá dứa sẽ đậm hơn nhưng nước sẽ có chút màu xanh phai ra từ lá. <br />
                  – Ngâm hạt é trong nước nóng khoảng 15 đến 20 phút, tới khi hạt é nở hết. <br />
                  – Rang hạnh nhân trong chảo chống dính ở lửa vừa tới khi hạnh nhân xém cạnh, vàng thơm. Đảo nhẹ tay trong khi rang, tránh để hạnh nhân bị vỡ vụn. Cho ra bát, để nguội. <br />
                  – Bóc nhãn, tách lấy riêng phần cùi nhãn. Có thể thay nhãn bằng vải hoặc các loại quả khác như dâu tây, dâu ta, đào, mận, xoài, mít… Nếu dùng vải hay nhãn hộp, nên giữ lại nước ngâm nhãn trong hộp để pha thêm vào nước đường lá nếp, sẽ làm nước đậm đà hơn. <br />
                  – Gỡ thạch ra khỏi khuôn: Dùng dao rọc quanh thành khuôn rồi gỡ thạch ra khỏi khuôn. Hoặc có thể nhúng nhanh khuôn vào nước nóng khoảng 10 giây, thạch sẽ tự tróc khỏi khuôn. Cắt thạch thành miếng vừa ăn. có thể dùng dao lưỡi răng cưa để cắt miếng thạch đẹp hơn. Nhúng dao vào nước trước khi cắt sẽ giúp cắt thạch dễ và đường cắt sắc nét, đẹp hơn.',
            '', 2, 17);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (110, '<b> Làm thạch rau câu nhân phô-mai </b>',
            '', 1, 18);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (111, 'Để riêng từng phần nước cà phê, nước sy-rô dâu, nước chanh leo và nước lá dứa vào trong các bát. Hoà ½ thìa cafe bột Agar vào từng bát, để tối thiểu 1 giờ cho bột nở. Việc ngâm bột thạch trong 1 giờ này sẽ giúp thạch rau câu không bị chảy nước sau khi đông. <br />
                  * Lưu ý: Tất cả các loại nước trên đều phải nguội khi pha bột Agar vào.',
            '', 2, 18);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (112, 'Cắt pho-mát bò cười thành từng miếng nhỏ vừa ăn. Xếp pho-mát bò cười vào các ô của khay làm đá viên. Nếu không có khay, bạn có thể xếp pho-mát vào hộp hay khay vuông, để chừa khoảng cách 1.5 – 2 cm giữa các viên pho-mát.',
            '', 3, 18);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (113, 'Quấy đều một phần nước ngâm bột thạch, đổ vào nồi. Đun ở lửa to. Quấy liên tục trong khi đun. Khi nước bay hơi rồi bắt đầu sôi lăn tăn thì bắc khỏi bếp (lúc này bột thạch đã tan hoàn toàn). Nêm đường cho vừa ăn, quấy cho đường tan hết. Với mỗi phần nước trong công thức, mình dùng 20 – 30 gram đường tuỳ độ đắng hay chua của nước.',
            '', 4, 18);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (114, 'Đổ thạch vào khay. Hớt bọt trên mặt thạch nếu có. Phần thạch còn dư đổ ra bát hoặc hộp vuông để làm thạch không có nhân.',
            '', 5, 18);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (115, 'Làm tương tự với các phần thạch còn lại.',
            '', 6, 18);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (116, 'Khi thạch nguội và tương đối đông thì để thạch vào tủ lạnh tới khi thạch đông cứng hẳn (mất khoảng 1 giờ).',
            '', 7, 18);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (117, 'Làm trà sữa',
            '', 8, 18);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (118, 'Cho các túi trà vào bình, đổ nước sôi để hãm trà.',
            '', 9, 18);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (119, 'Khi nước trà còn ấm nóng, cho sữa đặc và sữa bột vào, quấy đều cho sữa tan. Lượng sữa đặc có thể thay đổi tuỳ khẩu vị nhưng không nên cho quá nhiều, vị sữa sẽ át vị trà. Nên dùng sữa bột vì sữa bột giúp cho trà có vị béo ngậy và thơm hơn và không át đi mùi trà quá nhiều như sữa đặc.',
            '', 10, 18);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (120, 'Thêm đường tuỳ khẩu vị. Quấy cho tan đều. Để trà nguội rồi để ngăn mát tủ lạnh tới khi dùng.',
            '', 11, 18);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (121, '<b> Làm trân châu </b>',
            '', 12, 18);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (122, 'Cho bột năng và bột gạo tẻ vào bát, trộn đều rồi lấy khoảng 1 – 2 thìa canh (10 – 20 gram), để riêng. Phần bột này ta sẽ dùng để làm bột áo chống dính khi nhồi và nặn bột.',
            '', 13, 18);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (123, 'Rây bột cacao và đường vào bát, trộn thật đều.',
            '', 14, 18);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (124, 'Từ từ đổ nước sôi vào bát, vừa đổ vừa quấy liên tục. Khi bột quyện thành một khối thì không cho thêm nước nữa. Đợi bột nguội bớt rồi dùng tay nhồi nhẹ nhàng. Thêm bột áo nếu bột quá ướt và dính (dùng phần bột để riêng ở bước 1). Nhồi tới khi chúng ta có một khối bột mềm dẻo, hầu như không dính tay. <br />
                  * Lưu ý: <br />
                  – Bắt buộc phải dùng nước sôi. Nếu nước không đủ nóng, bột sẽ bị chảy nhão. <br />
                  – Lượng nước có thể thay đổi tuỳ loại bột. Nên cho nước từ từ từng chút một và theo dõi, khi bột bắt đầu quyện thành khối thì dừng lại. Nếu lỡ tay cho quá nhiều nước thì chỉ cần thêm bột năng cho tới khi bột dẻo, không dính tay là được. <br />
                  – Bột gạo trong công thức giúp cho trân châu có vị dai giòn và hơi cứng một chút. Nếu không có bột gạo trân châu sẽ vẫn dai nhưng mềm hơn. Các bạn có thể thử cả hai loại để chọn loại hợp với khẩu vị của mình hơn nhé.',
            '', 14, 18);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (125, 'Ngắt từng mẩu bột nhỏ, viên thành viên tròn nhỏ đường kính khoảng 0.8 – 1 cm. Bạn có thể xoa ít bột năng lên tay để chống dính. Làm tới khi hết bột.',
            '', 15, 18);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (126, 'Đun sôi một nồi nước to. Khi nước sôi mạnh thì thả tất cả bột vào luộc ở lửa to. Dùng thìa quấy nhẹ cho bột không bị dính vào nhau hay dính vào đáy nồi. Khi bột nổi lên mặt nước thì hạ lửa vừa, đun thêm khoảng 1 – 2 phút tới khi bột mềm thì vớt bột ra.',
            '', 16, 18);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (127, 'Nhẹ nhàng tách quả trứng làm đôi. Cố gắng giữ vỏ trứng làm 2 nửa nguyên vẹn nhé, bởi bạn sẽ còn sử dụng chúng đấy',
            '', 1, 19);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (128, 'Tách riêng lòng trắng và lòng đỏ. Giữ phần lòng đỏ trong nửa vỏ trứng, lòng trắng bạn nên để dành dùng cho các món ăn khác hoặc làm mặt nạ dưỡng da.',
            '', 2, 19);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (129, 'Cho muối vào phần vỏ trứng không chứa lòng đỏ, rải đều. Sau đó cho lòng đỏ vào',
            '', 3, 19);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (130, 'Bước kế tiếp, rắc một lớp muối lên lòng đỏ, trải đều muối khắp mặt lòng đỏ. Bạn cứ ước chừng mỗi lòng đỏ sẽ sử dụng khoảng 10g muối.',
            '', 4, 19);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (131, 'Cho trứng vào khay, dùng màng bọc thực phẩm bọc lại, cất vào ngăn mát tủ lạnh rồi để qua đêm.',
            '', 5, 19);
insert into direction(id, description, picture_url, step, recipe_id)
    VALUES (132, 'Mỗi khi sử dụng, bạn chỉ cần tách lòng đỏ ra khỏi vỏ, sau đó rửa sạch dưới vòi nước lạnh rồi chế biến. Nếu dùng không hết, bạn có thể cất vào ngăn đông, khi dùng lại rã đông như bình thường.',
            '', 5, 19);